const sushiSwapRouter = new web3.eth.Contract(
  sushiSwapRouterABI,
  sushiSwapRouterAddress
);

async function checkPair(args) {
  const {
    inputTokenSymbol,
    inputTokenAddress,
    outputTokenSymbol,
    outputTokenAddress,
    uniswapPairAddress,
    sushiSwapPairAddress,
    inputAmount,
    pairABI,
  } = args;

  // get uniswap pair contract
  const uniswapPairContract = new web3.eth.Contract(
    JSON.parse(pairABI),
    uniswapPairAddress
  );
  // get uniswap pair reservers
  const uniswapReservers = await uniswapPairContract.methods
    .getReserves()
    .call();

  let uniswap_reserve0;
  let uniswap_reserve1;

  if (outputTokenSymbol == "USDC") {
    uniswap_reserve0 = BigInt(
      Number(uniswapReservers._reserve0) * 1000000000000
    ).toString();
    uniswap_reserve1 = uniswapReservers._reserve1;
  } else if (outputTokenSymbol == "USDT") {
    uniswap_reserve0 = uniswapReservers._reserve0;
    uniswap_reserve1 = BigInt(
      Number(uniswapReservers._reserve1) * 1000000000000
    ).toString();
  } else if (outputTokenSymbol == "WBTC") {
    uniswap_reserve0 = BigInt(
      Number(uniswapReservers._reserve0) * 10000000000
    ).toString();
    uniswap_reserve1 = uniswapReservers._reserve1;
  } else {
    uniswap_reserve0 = uniswapReservers._reserve0;
    uniswap_reserve1 = uniswapReservers._reserve1;
  }
  // get uniswap prices
  const uniswapResult01 = await uniswapRouter.methods
    .getAmountOut(inputAmount, uniswap_reserve1, uniswap_reserve0)
    .call();
  const uniswapResult02 = await uniswapRouter.methods
    .getAmountOut(inputAmount, uniswap_reserve0, uniswap_reserve1)
    .call();

  // get sushiswap pair contract
  const sushiSwapPairContract = new web3.eth.Contract(
    JSON.parse(pairABI),
    sushiSwapPairAddress
  );

  // get sushiswap pair reservers
  const sushiSwapReservers = await sushiSwapPairContract.methods
    .getReserves()
    .call();

  let sushiSwap_reserve0;
  let sushiSwap_reserve1;
  if (outputTokenSymbol == "USDC") {
    sushiSwap_reserve0 = BigInt(
      Number(sushiSwapReservers._reserve0) * 1000000000000
    ).toString();
    sushiSwap_reserve1 = sushiSwapReservers._reserve1;
  } else if (outputTokenSymbol == "USDT") {
    sushiSwap_reserve0 = sushiSwapReservers._reserve0;
    sushiSwap_reserve1 = BigInt(
      Number(sushiSwapReservers._reserve1) * 1000000000000
    ).toString();
  } else if (outputTokenSymbol == "WBTC") {
    sushiSwap_reserve0 = BigInt(
      Number(sushiSwapReservers._reserve0) * 10000000000
    ).toString();
    sushiSwap_reserve1 = sushiSwapReservers._reserve1;
  } else {
    sushiSwap_reserve0 = sushiSwapReservers._reserve0;
    sushiSwap_reserve1 = sushiSwapReservers._reserve1;
  }

  // get sushiswap prices

  const sushiSwapResult01 = await sushiSwapRouter.methods
    .getAmountOut(inputAmount, sushiSwap_reserve1, sushiSwap_reserve0)
    .call();
  const sushiSwapResult02 = await sushiSwapRouter.methods
    .getAmountOut(inputAmount, sushiSwap_reserve0, sushiSwap_reserve1)
    .call();

  ++i;
  console.table([
    {
      "No:": i,
      "Input Token": inputTokenSymbol,
      "Output Token": outputTokenSymbol,
      "Input Amount": web3.utils.fromWei(inputAmount, "Ether"),
      "Uniswap V2": web3.utils.fromWei(uniswapResult01, "Ether"),
      "SushiSwap ": web3.utils.fromWei(sushiSwapResult01, "Ether"),
      Timestamp: moment().tz("America/Chicago").format(),
    },
  ]);
  savedata(
    `insert into savedprices (inputtoken,outputtoken,inputamount,uniswapv2,sushiswap,timestamp) values('${inputTokenSymbol}','${outputTokenSymbol}','${web3.utils.fromWei(
      inputAmount,
      "Ether"
    )}','${web3.utils.fromWei(uniswapResult01, "Ether")}','${web3.utils.fromWei(
      sushiSwapResult01,
      "Ether"
    )}','${moment().tz("America/Chicago").format()}')`
  );
  ++i;

  async function savedata(query) {
    const res = await db.query(query);
    //  const res= await db.query(`insert into savedprices (inputtoken,outputtoken,inputamount,uniswapv2,sushiswap,timestamp) values('${inputTokenSymbol}','${outputTokenSymbol}','${web3.utils.fromWei(inputAmount, 'Ether')}','${web3.utils.fromWei(uniswapResult02, 'Ether')}','${web3.utils.fromWei(sushiSwapResult02, 'Ether')}','${moment().tz('America/Chicago').format()}')`)
    if (!res.length < 1 || !res == undefined) {
      console.log("Error :");
      throw res;
    }
  }

  console.table([
    {
      "No:": i,
      "Input Token": outputTokenSymbol,
      "Output Token": inputTokenSymbol,
      "Input Amount": web3.utils.fromWei(inputAmount, "Ether"),
      "Uniswap V2": web3.utils.fromWei(uniswapResult02, "Ether"),
      "SushiSwap ": web3.utils.fromWei(sushiSwapResult02, "Ether"),
      Timestamp: moment().tz("America/Chicago").format(),
    },
  ]);
  savedata(
    `insert into savedprices (inputtoken,outputtoken,inputamount,uniswapv2,sushiswap,timestamp) values('${inputTokenSymbol}','${outputTokenSymbol}','${web3.utils.fromWei(
      inputAmount,
      "Ether"
    )}','${web3.utils.fromWei(uniswapResult02, "Ether")}','${web3.utils.fromWei(
      sushiSwapResult02,
      "Ether"
    )}','${moment().tz("America/Chicago").format()}')`
  );
}

